INSERT INTO `checkup_types` (`id`, `title`, `subtitle`, `description`, `content_1`, `content_2`, `file`, `status`, `sort_id`, `created_at`, `updated_at`) VALUES
(1, 'Взрослые Check-up пакеты', 'Комплексная проверка вашего здоровья за 1 день', 'Подготовка (Просим внимательно ознакомиться!) \nНапоминаем Вам, на время обследования для Вашего удобства одежда должна быть свободная (например, спортивная), без металлических вставок и замочков. Вам необходимо помнить, что накануне взятия анализа необходимо исключить: ', '<ul>\n<li>* физические нагрузки (не менее чем за 24 часа)&nbsp;</li>\n<li>* прием алкоголя (не менее чем за 24 часа)&nbsp;</li>\n<li>* жирной и газообразующей пищи (яблоки, виноград, черных хлеб), а также психологические стрессы</li>\n<li>* последний прием пищи должен быть не позднее 19:00 вечера накануне.</li>\n<li>* прийти необходимо натощак&nbsp;</li>\n<li>* утром не опорожнять мочевой пузырь (сбор мочи будет проходить в клинике), дома предварительно провести санацию половых органов.</li>\n</ul>', '<p><span style=\"color: #808080;\">Внимание:</span></p>\n<ul>\n<li><span style=\"color: #808080;\">* Также просим подготовить вопросы, которые Вы бы хотели задать врачу.</span></li>\n<li><span style=\"color: #808080;\">* Настоятельно просим не опаздывать, чтобы не нарушать весь последующий график обследований.</span></li>\n<li><span style=\"color: #808080;\">* Также во время проведения обследований просим ограничить курение.</span></li>\n</ul>', '[{\"download_link\":\"checkup-types\\/June2021\\/nZ3DnIeTDnbDRDC111aB.rar\",\"original_name\":\"Preparing for the Check up program.rar\"}]', 1, 1, '2021-06-04 07:18:00', '2021-07-15 02:41:26'),
(2, 'Детские Check-up пакеты', 'Комплексная проверка вашего здоровья за 1 день', 'Напоминаем Вам, на время обследования для Вашего удобства одежда должна быть свободная и удобная (халат и тапочки предоставляет клиника). ', '<ul>\n<li>* Не кормить ребенка утром, прийти&nbsp; &nbsp;на голодный желудок.</li>\n<li>* Ребенку необходимо провести гигиенические процедуры (подмыть).</li>\n<li>* Затем собрать среднюю порцию утренней мочи и утренний&nbsp; &nbsp;или вечерний кал. Если вечерний кал, то его необходимо разместить в контейнер, завернуть в целофановый мешочек&nbsp; &nbsp;и положить холодильник.&nbsp;</li>\n</ul>', '<p><span style=\"color: #808080;\">Внимание:</span></p>\n<ul>\n<li><span style=\"color: #808080;\">* Также просим подготовить вопросы, которые Вы бы хотели задать врачу. </span></li>\n<li><span style=\"color: #808080;\">* Настоятельно просим не опаздывать, чтобы не нарушать весь последующий график обследований. </span></li>\n<li><span style=\"color: #808080;\">* Также во время проведения обследований просим ограничить курение.</span></li>\n</ul>', '[{\"download_link\":\"checkup-types\\/June2021\\/oHz3tOXSpTGaq7zcerM7.rar\",\"original_name\":\"Preparing for the Check up program (Healthy child).rar\"}]', 1, 2, '2021-06-04 07:20:00', '2021-07-15 02:41:04'),
(3, 'Фокусные Check-up пакеты', 'Комплексная проверка вашего здоровья за 1 день', 'Подготовка к диагностическому обследованию Правильная подготовка к диагностическому обследованию служит залогом для успешного проведения Check Up и получения точных результатов:', '<ul>\n<li>* Не завтракать, не пить чай или кофе, не курить;</li>\n<li>* Не наносить кремы или лосьоны на кожу перед электрокардиограммой (ЭКГ);</li>\n<li>* Не рекомендованы физические нагрузки накануне CheckUp;</li>\n<li>* Не употреблять алкоголь за 2 дня до сдачи анализов;</li>\n<li>* Рекомендуется иметь при себе копию медицинской карты с результатами предыдущих анализов, если они сохранились;</li>\n<li>* Для проведения теста с нагрузкой необходимо иметь спортивный костюм;</li>\n<li>* Рекомендуем приходить на CheckUp без украшений.</li>\n</ul>', '<p>Внимание:</p>\n<ul>\n<li>* Утром до сдачи анализов не принимайте: гормоны, кортизоны, инсулин, кроворазжижающие (антикоагулянты), но имейте их при себе для приема после сдачи анализов.</li>\n<li>* За три дня до диагностики приостановить прием витаминов, железа, различных пищевых добавок.</li>\n<li>* Не прекращайте употребление лекарств, связанных с хроническими заболеваниями, такими как: сахарный диабет, гипертония, заболевание сердца, щитовидной железы, для снижения уровня холестерина, ревматические заболевания, психиатрические и неврологические заболевания.</li>\n<li>* Никаких физических и эмоциональных перегрузок.</li>\n</ul>', '[{\"download_link\":\"checkup-types\\/August2021\\/lC7v6nIH0Q8SiomQLWAG.\",\"original_name\":\"Preparing for the Check up program\"}]', 1, 3, '2021-06-04 07:52:00', '2021-08-16 08:47:02');
